mapper.select:
SELECT RES.* , DAMO.DECRYPT_VAR_B64('AES_256',CID, '') USERPHONE FROM (
SELECT /* com.ceragem.crm.backcus.dao.CrmUserInfoBaseDao.collabiSelectList */ * FROM ( SELECT SEQ COLLABISEQ /*SEQ */ , YMD COUNSELDT /*상담일자 */ , AGENT_NM AGENTNM /*상담원명 */ , CID /*고객번호 */ , HMS COLLABIHMS /*HMS */ , CLIENT_NM CLIENTNM /*고객명 */ FROM ONLYDB.LIST_COUNSEL )
AND CLIENTNM = #{userName}
AND DAMO.DECRYPT_VAR_B64('AES_256',CID,'') LIKE '%${userPhoneNumber}%'
ORDER BY CLIENTNM, COUNSELDT DESC
) RES

select.if:
SELECT /* com.ceragem.crm.backcus.dao.CrmUserInfoBaseDao.selectListCount */ COUNT(1) FROM ONLYDB.LIST_COUNSEL
AND CLIENT_NM = #{userName}
AND DAMO.DECRYPT_VAR_B64('AES_256',CID,'') LIKE '%${userPhoneNumber}%'

mapper.select:

