 FROM ONLYDB.USERS
 WHERE ID = #{userNum}

SELECT /*com.ceragem.crm.backcus.dao.CrmUserInfoBaseDao.collabiSelect */
SEQ COLLABISEQ , YMD COUNSELDT , AGENT_NM AGENTNM , CLIENT_NM CLIENTNM , QUESTION CLIENTQ , ANSWER CLIENTA , DAMO.DECRYPT_VAR_B64('AES_256',CID, '') USERPHONE
FROM ONLYDB.LIST_COUNSEL WHERE SEQ = #{collabiSeq}
