	 AND A.CUST_STATUS_CD != '003'

          AND A.BOS_CNTRT_CNT     >=       #{bosCntrtCnt}


          AND A.RCMD_CNT     >=       #{rcmdCnt}





            AND A.CUST_TYPE_CD    =       #{custTypeCd}


