mapper.sql:
SELECT 
	STOR_NO,
	STOR_NM,
	DAMO.DECRYPT_VAR_B64('AES_256', TEL_NO, '') AS TEL_NO,
	STOR_STATUS_CD,
	DISTRCT_CD,
	REG_DT
FROM CRM_STOR_BAS

	
		AND STOR_NO = #{storNo}
	
	
		AND STOR_NM LIKE '%' || #{storNm} || '%'
	
	
		AND STOR_DIV_CD = #{storDivCd}
	
	
		AND STOR_STATUS_CD = #{storStatusCd}
	
	
		
			
				AND STOR_CHL_CD = #{storChlCd}
			
			
				AND STOR_CHL_CD IN
				
					#{item}
				
			
		
	
	
		
			
				AND DISTRCT_CD = #{distrctCd}
			
			
				AND DISTRCT_CD IN
				
					#{item}

mapper.select:
/* com.ceragem.crm.mem.dao.CrmStoreBasDao.selectStoreBasList */
SELECT STOR_NO, STOR_NM, TEL_NO, STOR_STATUS_CD, DISTRCT_CD, DISTRCT_CD AS DISTRCT_NM, REG_DT, NVL(MEMBER_CNT, 0) AS MEMBER_CNT FROM(
) sTB LEFT JOIN( SELECT RCMD_STOR_NO, COUNT(1) AS MEMBER_CNT FROM CRM_CUST_BAS GROUP BY RCMD_STOR_NO ) cTB ON(sTB.STOR_NO = cTB.RCMD_STOR_NO) ORDER BY STOR_NO DESC

mapper.sql:
SELECT ITG_CUST_NO, MSHIP_LOGIN_ID, CUST_NM, GNDR_CD, BIRTHDAY, MPHON_NO, MPHON_NO_MASKING, REMAIN_POINT_SCORE, totalTB.GODS_NO, GODS_NM, ORD_DATE, totalTB.REG_DT FROM( SELECT rnkTB.* FROM( SELECT custTB.ITG_CUST_NO, custTB.MSHIP_LOGIN_ID, custTB.CUST_NM, custTB.GNDR_CD, custTB.BIRTHDAY, custTB.MPHON_NO, NVL2(custTB.MPHON_BK_DGT_NO, '010-****-'|| custTB.MPHON_BK_DGT_NO, '') AS MPHON_NO_MASKING, ( TO_NUMBER(TO_CHAR(SYSDATE, 'YYYY')) - TO_NUMBER(SUBSTRB(custTB.BIRTHDAY, 0, 4)) ) AS AGE, NVL(REMAIN_POINT_SCORE, 0) AS REMAIN_POINT_SCORE, BASE_ITEM_CD AS GODS_NO, ORD_DE AS ORD_DATE, custTB.REG_DT, RANK() OVER (PARTITION BY buyTB.ITG_CUST_NO ORDER BY CNTR_NO DESC) AS RNK FROM CRM_CUST_BAS custTB LEFT JOIN CRM_CUST_BOS_CNTRT_HST buyTB ON(custTB.ITG_CUST_NO = buyTB.ITG_CUST_NO AND ORD_STS_CD = '30') WHERE RCMD_STOR_NO = #{storNo}
AND custTB.CUST_NM LIKE '%' || #{custNm} || '%'
AND custTB.GNDR_CD = #{gndrCd}
AND custTB.ITG_CUST_NO = #{itgCustNo}
AND custTB.MPHON_NO = DAMO.ENCRYPT_VAR_B64('AES_256', #{mphonNo}, '')
) rnkTB WHERE RNK = 1
AND AGE >= TO_NUMBER(#{ageStt})
AND AGE < TO_NUMBER(#{ageEnd})+10
) totalTB LEFT JOIN CRM_GODS_BAS godsTB ON(totalTB.GODS_NO = godsTB.GODS_NO)

mapper.update:
/* com.ceragem.crm.mem.dao.CrmStoreBasDao.updateStoreAllCustMove */
UPDATE CRM_CUST_BAS
SET RCMD_STOR_NO = #{storNo}
WHERE RCMD_STOR_NO IS NOT NULL
AND RCMD_STOR_NO = #{currStorNo}

/* com.ceragem.crm.mem.dao.CrmStoreBasDao.updateStoreCustMove */
UPDATE CRM_CUST_BAS
SET RCMD_STOR_NO = #{storNo}
WHERE ITG_CUST_NO IN 

	
		#{item.itgCustNo}
