mapper.insert:

		    		SELECT FN_CRM_AUTO_SEQ('CPR') FROM DUAL

                INSERT INTO CRM_CRPT_CMP_BAS (

                 ) VALUES (
                       #{cprtCmpNo}
                     , #{cprtCmpNm}
                     , #{cprtStatusCd}
                     , #{cprtDivCd}
                     , DAMO.ENCRYPT_VAR_B64('AES_256',#{telNo},'')
                     , #{zipCd}
                     , #{addr1Ctnts}
                     , DAMO.ENCRYPT_VAR_B64('AES_256',#{addr2Ctnts},'')
                     , #{bizNo}
                     , #{cprtCmpTypeCd}
                     , #{cprtCmpMemoCtnts}
                     , #{cprtCmpRegYmd}
                     , #{cprtCmpChrgExecvdempNo}
                     , #{execvdempNm}
                     , #{deptNm}
                     , #{rspofNm}
                     , #{birthday}
                     , DAMO.ENCRYPT_VAR_B64('AES_256',#{picTelNo},'')
                     , #{gndrCd}
                     , DAMO.ENCRYPT_VAR_B64('AES_256',#{emailAddr},'')
                     , #{amdrId}
                     , SYSDATE
                     , #{regrId}
                     , SYSDATE
                     , #{regChlCd}
                 )

mapper.update:
UPDATE CRM_CRPT_CMP_BAS
   SET CPRT_CMP_NO         			=         #{cprtCmpNo}
     , CPRT_CMP_NM             		=         #{cprtCmpNm}
     , CPRT_STATUS_CD             	=         #{cprtStatusCd}
     , CPRT_DIV_CD            		=         #{cprtDivCd}
     , TEL_NO              			=         #{telNo}
     , ZIP_CD         				=         #{zipCd}
     , ADDR_1_CTNTS         			=         #{addr1Ctnts}
     , ADDR_2_CTNTS             		=         #{addr2Ctnts}
     , BIZ_NO              			=         #{bizNo}
     , CPRT_CMP_TYPE_CD            	=         #{cprtCmpTypeCd}
     , CPRT_CMP_MEMO_CTNTS     		=         #{cprtCmpMemoCtnts}
     , CPRT_CMP_REG_YMD          	=         #{cprtCmpRegYmd}
     , CPRT_CMP_CHRG_EXECVDEMP_NO	=         #{cprtCmpChrgExecvdempNo}
     , EXECVDEMP_NM        			=         #{execvdempNm}
     , DEPT_NM 						=         #{deptNm}
     , RSPOF_NM      				=         #{rspofNm}
     , BIRTHDAY						=         #{birthday}
     , PIC_TEL_NO					=         #{picTelNo}
     , GNDR_CD      					=         #{gndrCd}
     , EMAIL_ADDR 					=         #{emailAddr}
     , AMDR_ID             			=         #{amdrId}
     , AMD_DT              			=         SYSDATE
     , REG_CHL_CD          			=         #{regChlCd}

mapper.insert:
MERGE INTO CRM_CRPT_CMP_EXECVDEMP_BAS USING DUAL ON (CPRT_CMP_IDFY_NO = #{cprtCmpIdfyNo}) WHEN MATCHED THEN UPDATE SET CPRT_CMP_NO = #{cprtCmpNo}, MPHON_NO = DAMO.ENCRYPT_VAR_B64('AES_256',#{mphonNoEncVal},''), MPHON_BK_DGT_NO = #{mphonBkDgtNo}, CPRT_CMP_CARD_NO = #{cprtCmpCardNo}, EXECVDEMP_NM = #{execvdempNm}, AMDR_ID = #{amdrId}, AMD_DT = SYSDATE, REG_CHL_CD = #{regChlCd}
WHEN NOT MATCHED THEN INSERT ( CPRT_CMP_IDFY_NO , CPRT_CMP_NO , MPHON_NO , MPHON_BK_DGT_NO , CPRT_CMP_CARD_NO , EXECVDEMP_NM , AMDR_ID , AMD_DT , REGR_ID , REG_DT , REG_CHL_CD
) VALUES ( #{cprtCmpIdfyNo} , #{cprtCmpNo} , DAMO.ENCRYPT_VAR_B64('AES_256',#{mphonNoEncVal},'') , #{mphonBkDgtNo} , #{cprtCmpCardNo} , #{execvdempNm} , #{amdrId} , SYSDATE , #{regrId} , SYSDATE , #{regChlCd} )
